import React from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Menu,Input, Row,Col,Card,Avatar } from 'antd';
import LoginForm from '../pages/LoginForm';

const dummy ={
    nickname: 'SIPO',
    Post:[],
    Follwings:[],
    Followers:[],
    isLoggedIn: false,
}

const AppLayout= ({children})=>{
    return(
        <div>
            <Menu mode="horizontal">
                <Menu.Item key="home"><Link href="/"><a>NodeSNS</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
                <Menu.Item key="mail">
                    <Input.Search enterButton style={{ verticalAlign: 'middle' }} />
                </Menu.Item>
            </Menu>
          <Row>
              <Col xs={24} md={24}></Col>
          </Row>
            <Row gutter={[16, 16]}type="flex" justify="space-around" align="top" style={{margin:"1%"}}>
                <Col xs={8} md={6}>
                    {dummy.isLoggedIn ? <Card
                        actions={[
                            <div key="twit">포스트<br />{dummy.Post.length}</div>,
                            <div key="following">팔로윙<br />{dummy.Follwings.length}</div>,
                            <div key="follower">팔로워<br />{dummy.Followers.length}</div>,
                        ]}
                    >
                        <Card.Meta
                        avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
                        title={dummy.nickname}
                        />
                    </Card>
                    :
                   <LoginForm />
}
                </Col>
                <Col xs={8}md={12}>{children}</Col>
                <Col xs={8}md={6}>세번째줄</Col>
            </Row>
        </div>
    )
}

AppLayout.propTypes = {
    children: PropTypes.node,
}

export default AppLayout;
